
const apiKey = "7c9f4cec06a27c85c0cd817d09ef5bc3";
const apiUrl = "https://api.openweathermap.org/data/2.5/weather?units=metric&q=";

const searchBox = document.querySelector(".search input");
const searchBtn = document.querySelector(".search button");
const weatherIcon = document.querySelector(".weather-icon");

async function checkWeather(city) {
  try {
    const response = await fetch(apiUrl + city + `&appid=${apiKey}`);
    if (!response.ok) {
      throw new Error("City not found");
    }
    var data = await response.json();
    console.log(data);

    document.querySelector(".city").innerHTML = data.name;
    document.querySelector(".temp").innerHTML = Math.round(data.main.temp) + "Â°C";
    document.querySelector(".humidity").innerHTML = data.main.humidity + " %";
    document.querySelector(".wind").innerHTML = data.wind.speed + " km/hr";

    switch (data.weather[0].main.toLowerCase()) {
      case "clouds":
        weatherIcon.src = "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSrdi5LdwvxHV-GWb2xeVGtmN7Gw5Bp0NnzsQ&s";
        break;
      case "clear":
        weatherIcon.src = "data:image/jpeg;base64,";
        break;
      case "rain":
        weatherIcon.src = "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSwuffJO-mr_ip6nNyVzfwfuIfLwZr3TBL52Q&s";
        break;
      case "drizzle":
        weatherIcon.src = "data:image/jpeg;base64,";
        break;
      case "mist":
        weatherIcon.src = "data:image/png;base64,";
        break;
      default:
        weatherIcon.src = "data:image/png;base64,   "; // Add a default icon
        break;
    }
    document.querySelector(".weather").style.display = "block";
  } catch (error) {
    console.error(error);
    alert(error.message);
  }
}

searchBtn.addEventListener("click", () => {
  checkWeather(searchBox.value);
});
